mapper.select:
SELECT sysDept.v_dept_id, sysDept.v_parent_id, sysDept.v_dept_name, sysDept.i_dept_type_id, sysDept.dt_modifyTime, sysDept.i_status, sysDept.v_remarks, sysDept.v_tell, sysDept.v_address, sysDept.v_contacts, sysinfo.v_name_zh_cn AS typeName FROM tbl_sys_dept sysDept left join tbl_sys_systeminfo sysinfo on sysinfo.v_sysvalue5 = sysDept.i_dept_type_id and sysinfo.v_sysid = '10001'
v_parent_id = #{searchDetpListModule.vParentId}
and v_dept_name like CONCAT('%',#{searchDetpListModule.vDeptName},'%')
and sysDept.i_out_service = #{searchDetpListModule.iOutService}
and v_address like CONCAT('%',#{searchDetpListModule.vAddress},'%')
and i_dept_type_id = #{searchDetpListModule.iDeptTypeId}
and sysDept.v_dept_id
#{item}
and i_status = 0
ORDER BY CONVERT(sysDept.v_dept_name USING gbk) ASC

SELECT sysDept.*
FROM tbl_sys_dept sysDept
WHERE sysDept.v_dept_id = #{deptId}

select v_dept_id
from tbl_sys_dept
where v_parent_id = #{parentDeptId}
