mapper.select:
select village.v_village_id, village.v_village_name, village.v_address, village.v_project_id, project.v_project_name, village.v_longitude, village.v_latitude, village.v_remarks, village.dt_create_time, village.dt_modify_time, village.v_create_user_id, village.v_modify_user_id, village.v_img_url, village.i_del_flag, sysinfo.v_name_zh_cn AS i_del_flag_name from tbl_village village inner join tbl_elevator ele on ele.v_village_id = village.v_village_id
join tbl_sys_user_resource resource on ele.v_elevator_id = resource.v_resource_id and v_user_id = #{userId}
join tbl_project project on project.v_project_id = village.v_project_id join tbl_sys_dept dept on dept.v_dept_id = project.v_dept_id left join tbl_sys_systeminfo sysinfo on sysinfo.v_sysvalue5 = village.i_del_flag and sysinfo.v_sysid = '10012'
and village.v_village_id like CONCAT('%',#{vVillageId,jdbcType=VARCHAR},'%')
and village.v_village_name like CONCAT('%',#{vVillageName,jdbcType=VARCHAR},'%')
and village.v_address like CONCAT('%',#{vAddress,jdbcType=VARCHAR},'%')
and village.v_project_id = #{vProjectId,jdbcType=VARCHAR}
and village.v_remarks like CONCAT('%',#{vRemarks,jdbcType=VARCHAR},'%')
and village.i_del_flag = #{iDelFlag,jdbcType=INTEGER}
and project.v_dept_id
#{item}
group by village.v_village_id

select village.v_village_id, village.v_village_name, village.v_address, village.v_project_id, village.v_longitude, village.v_latitude, village.v_remarks, village.v_img_url, village.i_del_flag from tbl_village village inner join tbl_elevator ele on ele.v_village_id = village.v_village_id
join tbl_sys_user_resource resource on ele.v_elevator_id = resource.v_resource_id and v_user_id = #{userId}
join tbl_project project on project.v_project_id = village.v_project_id join tbl_sys_dept dept on dept.v_dept_id = project.v_dept_id
and village.v_village_id like CONCAT('%',#{vVillageId,jdbcType=VARCHAR},'%')
and village.v_village_name like CONCAT('%',#{vVillageName,jdbcType=VARCHAR},'%')
and village.v_address like CONCAT('%',#{vAddress,jdbcType=VARCHAR},'%')
and village.v_project_id = #{vProjectId,jdbcType=VARCHAR}
and village.v_remarks like CONCAT('%',#{vRemarks,jdbcType=VARCHAR},'%')
and village.i_del_flag = #{iDelFlag,jdbcType=INTEGER}
and project.v_dept_id
#{item}
and ele.i_install_status = 1
group by village.v_village_id

select village.v_village_id, village.v_village_name, village.v_address, village.v_img_url from tbl_village village inner join tbl_elevator ele on ele.v_village_id = village.v_village_id
join tbl_sys_user_resource resource on ele.v_elevator_id = resource.v_resource_id and v_user_id = #{userId}
village.v_project_id = #{projectId, jdbcType=VARCHAR}
group by village.v_village_id
