mapper.select:
select

from tbl_device_file


        and device_file_id = #{deviceFileId,jdbcType=VARCHAR}


        and file_name = #{fileName,jdbcType=VARCHAR}


        and version = #{version,jdbcType=VARCHAR}


        and file_url = #{fileUrl,jdbcType=VARCHAR}


        and request_file_url = #{requestFileUrl,jdbcType=VARCHAR}


        and label = #{label,jdbcType=VARCHAR}


        and signature = #{signature,jdbcType=VARCHAR}


        and crc = #{crc,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


        and create_user_id = #{createUserId,jdbcType=VARCHAR}


        and modify_user_id = #{modifyUserId,jdbcType=VARCHAR}


        and del_flag = #{delFlag,jdbcType=INTEGER}


        and eType = #{eType,jdbcType=VARCHAR}

