mapper.select:
select tbl.v_distribution_task_id, tbl.v_project_id, tbl.v_village_id, tbl.v_principal_id, tbl.i_elevator_count, tbl.i_status, tbl.i_next_step, tbl.v_init_elevator_code, tbl.v_init_verify_code, tbl.i_shortcut, tbl.i_cube_count, tbl.v_distributer_name, case when tbl.i_status = 1 then '未配货' when tbl.i_status = 2 then '配货中' when tbl.i_status = 3 then '完成' when tbl.i_status = -1 then '已取消' else '' end as i_status_name, tbl.v_template_file_url, tbl.v_address, tbl.dt_create_time, tbl.dt_modify_time, tbl.v_create_user_id, tbl.v_modify_user_id, tbl.i_del_flag, village.v_village_name, project.v_project_name, u.v_name as v_principal_name, file.dt_modify_time as upload_time, file.v_modify_user_id as upload_user_id, sysinfo.v_name_zh_cn AS i_del_flag_name, dept.v_dept_name from tbl_distribution_task tbl left join tbl_sys_user as u on u.v_user_id = tbl.v_principal_id left join tbl_pm_file as file on file.v_target_id = tbl.v_distribution_task_id left join tbl_project project on project.v_project_id = tbl.v_project_id left join tbl_sys_dept dept on dept.v_dept_id = project.v_dept_id left join tbl_village village on village.v_village_id = tbl.v_village_id left join tbl_sys_systeminfo sysinfo on sysinfo.v_sysvalue5 = tbl.i_del_flag and sysinfo.v_sysid = '10012'
and tbl.v_distribution_task_id = #{vDistributionTaskId,jdbcType=VARCHAR}
and tbl.v_project_id = #{vProjectId,jdbcType=VARCHAR}
and project.v_project_name like concat("%", #{projectName,jdbcType=VARCHAR}, "%")
and tbl.v_village_id = #{vVillageId,jdbcType=VARCHAR}
and village.v_village_name like concat("%", #{vVillageName,jdbcType=VARCHAR}, "%")
and tbl.v_address like CONCAT('%',#{vAddress,jdbcType=VARCHAR},'%')
and tbl.i_del_flag = #{iDelFlag, jdbcType=INTEGER}
and tbl.i_status = #{iStatus, jdbcType=INTEGER}
and tbl.v_principal_id = #{userId, jdbcType=VARCHAR}
order by tbl.dt_create_time desc

select 1 from tbl_elevator as elevator where elevator.v_elevator_code in
#{item}
and elevator.i_del_flag = 0 limit 0, 1
