mapper.select:
select tbl.v_installing_task_id, tbl.v_project_id, tbl.v_village_id, tbl.v_address, tbl.v_principal_id, tbl.d_installing_date, tbl.i_elevator_count, tbl.i_status, tbl.i_next_step, case when tbl.i_status = 1 then '未安装' when tbl.i_status = 2 then '安装中' when tbl.i_status = 3 then '完成' when tbl.i_status = -1 then '已取消' else '无' end as i_status_name, tbl.dt_create_time, tbl.dt_modify_time, tbl.v_create_user_id, tbl.v_modify_user_id, tbl.i_del_flag, village.v_village_name, project.v_project_name, sysinfo.v_name_zh_cn AS i_del_flag_name, u.v_name as v_principal_name, u.v_mobile as v_principal_mobile, dept.v_dept_name from tbl_installing_task tbl left join tbl_sys_user as u on u.v_user_id = tbl.v_principal_id left join tbl_project project on project.v_project_id = tbl.v_project_id left join tbl_sys_dept dept on dept.v_dept_id = project.v_dept_id left join tbl_village village on village.v_village_id = tbl.v_village_id left join tbl_sys_systeminfo sysinfo on sysinfo.v_sysvalue5 = tbl.i_del_flag and sysinfo.v_sysid = '10012'
and tbl.v_installing_task_id like concat("%", #{vInstallingTaskId,jdbcType=VARCHAR}, "%")
and tbl.v_project_id = #{vProjectId,jdbcType=VARCHAR}
and tbl.v_village_id = #{vVillageId,jdbcType=VARCHAR}
and tbl.v_address like CONCAT('%',#{vAddress,jdbcType=VARCHAR},'%')
and tbl.i_del_flag = #{iDelFlag, jdbcType=INTEGER}
and tbl.i_status in
#{item}
and tbl.v_principal_id = #{userId, jdbcType=VARCHAR}
order by tbl.dt_create_time desc
