mapper.select:
select

from tbl_fault_mapping


        and id = #{id,jdbcType=INTEGER}


        and platform_code = #{platformCode,jdbcType=VARCHAR}


        and platform_name = #{platformName,jdbcType=VARCHAR}


        and fault_type = #{faultType,jdbcType=VARCHAR}


        and fault_name = #{faultName,jdbcType=VARCHAR}


        and mx_fault_type = #{mxFaultType,jdbcType=VARCHAR}


        and mx_fault_name = #{mxFaultName,jdbcType=VARCHAR}


        and elevator_type = #{elevatorType,jdbcType=INTEGER}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and del_flag = #{delFlag,jdbcType=OTHER}

