mapper.select:
select `id`,`elevator_code`,`type_id`,`data`,`start_time` from pro_elevator_event_record

    and id = #{id}
    and elevator_code like '%${elevatorCode}%'
    and type_id = #{typeId}
    and data = #{data}
    and start_time >= #{startTime}
    and start_time <= #{endTime}
    and sensor_type = #{sensorType}




        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from pro_elevator_event_record

    and id = #{id}
    and elevator_code like '%${elevatorCode}%'
    and type_id = #{typeId}
    and data = #{data}
    and start_time >= #{startTime}
    and start_time <= #{endTime}
    and sensor_type = #{sensorType}

SELECT elevator_code,COUNT(id) AS cnt FROM pro_elevator_event_record

    and id = #{id}
    and elevator_code like '%${elevatorCode}%'
    and type_id = #{typeId}
    and data = #{data}
    and start_time >= #{startTime}
    and start_time <= #{endTime}
    and sensor_type = #{sensorType}

WHERE start_time > DATE_ADD(NOW(),INTERVAL -14 DAY)
GROUP BY elevator_code ORDER BY cnt DESC
limit 100

SELECT DATE_FORMAT (start_time,'%Y-%m-%d %H') AS date,COUNT(id) AS cnt FROM pro_elevator_event_record

    and id = #{id}
    and elevator_code like '%${elevatorCode}%'
    and type_id = #{typeId}
    and data = #{data}
    and start_time >= #{startTime}
    and start_time <= #{endTime}
    and sensor_type = #{sensorType}

WHERE start_time > DATE_ADD(NOW(),INTERVAL -7 DAY)
GROUP BY date
ORDER BY start_time asc;
