mapper.select:
select
`id`,`elevator_id`,`elevator_code`,`failure_id`,`failure_name`,`is_user_visible`,`is_report`,`create_user`,`create_time`,`update_user`,`update_time`,`del_flag`
from pro_failure_shield

    del_flag = 0
    and id = #{id}
    and elevator_id = #{elevatorId}
    and `elevator_code` = #{elevatorCode}
    and failure_id = #{failureId}
    and is_user_visible = #{isUserVisible}
    and is_report = #{isReport}
    and create_user = #{createUser}
    and create_time = #{createTime}
    and update_user = #{updateUser}
    and update_time = #{updateTime}
    and del_flag = #{delFlag}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}
